import React, { useState } from 'react';
import { Room } from '..';
import './Button.css';

const Button = ({ visibilityState }) => {

    const [visible, setVisible] = useState(visibilityState);

    const lightSwitch = () => {
        setVisible(false);
        return <Room style={{
            backgroundColor: `${visible ? "bisque" : "black"}`
        }} />
    }

    return (
        <div className="Button">
            <button className="btn"
                onClick={lightSwitch}
            >

                Switch On
            </button>
            <button className="btn">Switch Off</button>
        </div>
    )
}

export default Button;
