import React, { useState } from 'react';
import { Button } from '..';
import './Room.css';


const Room = ({ visibilityState, backgroundColor }) => {

    const [visible, setVisible] = useState(visibilityState);

    const lightSwitch = () => {

        setVisible(false) ?
            document.querySelector('.partTwo').backgroundColor = 'red'
            :
            document.querySelector('.partTwo').backgroundColor = 'yellow'
    }


    return (
        <div className="Room"
            style={{
                backgroundColor: `${visible ? "bisque" : "black"}`
            }}
        >

            <div className="Light" >
                <div className="partOne"></div>
                <div className="partTwo"></div>
                <Button onClick={lightSwitch} />
            </div>


        </div>
    )
}

export default Room;
