import React, { useState } from 'react';
import { Button } from '..';
import './Room.css';


const Room = ({ visibilityState }) => {

    const [visible, setVisible] = useState(visibilityState);

    const lightSwitch = (boolean) => {
        setVisible(boolean)
    }


    return (
        <div className="Room"
            style={{
                backgroundColor: `${visible ? "bisque" : "black"}`
            }}
        >

            <div className="Light">
                <div className="partOne"></div>
                <div className="partTwo"
                    style={{
                        display: `${visible ? "block" : "none"}`
                    }}
                ></div>
                <Button
                    title='Switch On'
                    onClick={lightSwitch(true)}
                />
                <br />
                <Button
                    title='Switch Off'
                    onClick={lightSwitch(false)}
                />
            </div>
        </div>
    )
}

export default Room;
