import './Room.css';
import React, { useState } from 'react';

const Room = ({ visibilityState }) => {

    const [visible, setVisible] = useState(visibilityState);

    const lightSwitch = () => {
        setVisible(true);
    }

    return (
        <div className="Room"
            style={{
                backgroundColor: `${visible ? "bisque" : "black"}`,
            }}
        >
            <div className="Light" onClick={lightSwitch}>
                <div className="partOne"></div>
                <div className="partTwo"></div>
            </div>
        </div>
    )
}

export default Room;
